import { useState } from "react";
import "./App.css";

function App() {
  return (
    <div className="toggler">
      <RatingComp />
    </div>
  );
}

function RatingComp() {
  let [act, acts] = useState("0");

  function setAct() {
    acts((act = document.querySelectorAll("button").textContent));
    console.log(document.querySelector("button").textContent);
  }

  return (
    <div className="main-container">
      <div className="star-container">
        <div className="star">â˜…</div>
      </div>
      <div className="info">
        <h1>How did we do?</h1>
        <p>
          Please let us know how we did with your support request. All feedback
          is appreciated to help us improve our offering!
        </p>
      </div>
      <div className="button-container">
        <button className={act == "1" ? "btn active" : "btn"} onClick={setAct}>
          1
        </button>
        <button className={act == "2" ? "btn active" : "btn"} onClick={setAct}>
          2
        </button>
        <button className={act == "3" ? "btn active" : "btn"} onClick={setAct}>
          3
        </button>
        <button className={act == "4" ? "btn active" : "btn"} onClick={setAct}>
          4
        </button>
        <button className={act == "5" ? "btn active" : "btn"} onClick={setAct}>
          5
        </button>
      </div>
      <div className="submit-button-container">
        <button className="submit">SUBMIT</button>
      </div>
    </div>
  );
}
export default App;
